function radixSort(arr) {
  const max = Math.max(...arr);
  for (let exp = 1; max / exp > 0; exp *= 10) {
    const output = Array(arr.length).fill(0);
    const count = Array(10).fill(0);
    for (let i = 0; i < arr.length; i++) count[Math.floor(arr[i] / exp) % 10]++;
