```javascript
class History {
  constructor() {
    this.undoStack = [];
    this.redoStack = [];
  }

  add(state) {
    this.undoStack.push(state);
    this.redoStack = [];
  }

  undo() {
    if (this.undoStack.length) {
      const state = this.undoStack.pop();
      this.redoStack.push(state);
      return this.undoStack[this.undoStack.length - 1];
