function debounceQueue(fn, delay) {
  let queue = [];
  let timer;

  return function(...args) {
    queue.push(args);
    clearTimeout(timer);
    timer = setTimeout(() => {
      fn(queue);
      queue = [];
    }, delay);
  };
}
